## lattice element からコンダクタンスを計算する。function retval = vac_conductance(elem, half)  if (nargin > 1)    hflag = strcmp(half, "half");  else    hflag = false;  end    cross_section = elem.duct;  if hflag    l = elem.len/2;  else    l = elem.len;  end    switch cross_section.shape    case "rect"      dx = cross_section.xmax - cross_section.xmin;      dy = cross_section.ymax - cross_section.ymin;      k = coeff_rect(dx, dy);      retval = 309*k*dx^2*dy^2/((dx+dy)*l);    case "circle"      d = 2*cross_section.radius;      k = coeff_circle(d,l);      retval = k*116*(pi*(d/2)^2);    otherwise      error("Unknown shape.");  endswitchendfunction